import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Users, Plus, Trash2 } from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import StatusIndicator from "@/components/common/status-indicator";
import { Link } from "wouter";

export default function UserMappingsWidget() {
  const { toast } = useToast();

  const { data: userMappings, isLoading } = useQuery({
    queryKey: ["/api/user-mappings"],
    refetchInterval: 60000,
  });

  const deleteUserMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/user-mappings/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user-mappings"] });
      toast({
        title: "User Removed",
        description: "User mapping deleted successfully"
      });
    },
    onError: () => {
      toast({
        title: "Delete Failed",
        description: "Unable to delete user mapping",
        variant: "destructive"
      });
    }
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader className="border-b border-border">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center">
              <Users className="mr-2 text-primary" />
              User Mappings
            </CardTitle>
            <div className="h-8 w-20 bg-muted rounded animate-pulse"></div>
          </div>
        </CardHeader>
        <CardContent className="p-6">
          <div className="space-y-3">
            {Array.from({ length: 2 }).map((_, i) => (
              <div key={i} className="flex items-center justify-between p-3 bg-secondary/30 rounded-lg animate-pulse">
                <div>
                  <div className="h-4 bg-muted rounded w-32 mb-2"></div>
                  <div className="h-3 bg-muted rounded w-24"></div>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-muted rounded-full"></div>
                  <div className="w-4 h-4 bg-muted rounded"></div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  const mappings = userMappings || [];

  return (
    <Card>
      <CardHeader className="border-b border-border">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center">
            <Users className="mr-2 text-primary" />
            User Mappings
          </CardTitle>
          <Link href="/user-mappings">
            <Button size="sm" data-testid="button-add-user">
              <Plus className="w-4 h-4 mr-1" />
              Add User
            </Button>
          </Link>
        </div>
      </CardHeader>
      <CardContent className="p-6">
        <div className="space-y-3">
          {mappings.length > 0 ? (
            mappings.slice(0, 3).map((mapping: any) => (
              <div key={mapping.id} className="flex items-center justify-between p-3 bg-secondary/30 rounded-lg">
                <div>
                  <p className="text-sm font-medium text-foreground">{mapping.email}</p>
                  <p className="text-xs text-muted-foreground">User ID: {mapping.dahuaUserId}</p>
                </div>
                <div className="flex items-center space-x-2">
                  <StatusIndicator status={mapping.isActive ? 'online' : 'offline'} />
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => deleteUserMutation.mutate(mapping.id)}
                    disabled={deleteUserMutation.isPending}
                    className="text-destructive hover:text-destructive/80 p-1"
                    data-testid={`button-remove-user-${mapping.id}`}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            ))
          ) : (
            <div className="flex items-center justify-center p-6 border-2 border-dashed border-border rounded-lg">
              <div className="text-center">
                <p className="text-sm text-muted-foreground">No users mapped</p>
                <Link href="/user-mappings">
                  <Button variant="ghost" size="sm" className="text-xs text-primary hover:text-primary/80 mt-1">
                    Add your first user mapping
                  </Button>
                </Link>
              </div>
            </div>
          )}
          {mappings.length > 3 && (
            <Link href="/user-mappings">
              <Button variant="ghost" className="w-full text-sm text-primary hover:text-primary/80">
                View All Users ({mappings.length})
              </Button>
            </Link>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/layout/sidebar.tsx.content:
import { Link, useLocation } from "wouter";
import { Shield, Activity, Users, DoorOpen, Settings, FlaskConical } from "lucide-react";
import { cn } from "@/lib/utils";
import StatusIndicator from "@/components/common/status-indicator";
import { useQuery } from "@tanstack/react-query";

const navigation = [
  { name: "Dashboard", href: "/", icon: Activity },
  { name: "Access Logs", href: "/access-logs", icon: Activity },
  { name: "User Mappings", href: "/user-mappings", icon: Users },
  { name: "Room Mappings", href: "/room-mappings", icon: DoorOpen },
  { name: "Configuration", href: "/configuration", icon: Settings },
  { name: "Test Endpoints", href: "/test-endpoints", icon: FlaskConical },
];

export default function Sidebar() {
  const [location] = useLocation();

  const { data: systemHealth } = useQuery({
    queryKey: ["/api/system-health"],
    refetchInterval: 30000,
  });

  const getServiceStatus = (serviceName: string) => {
    if (!systemHealth || !Array.isArray(systemHealth)) return 'offline';
    const service = systemHealth.find((s: any) => s.service === serviceName);
    return service?.status || 'offline';
  };

  return (
    <aside className="w-64 bg-card border-r border-border flex flex-col">
      {/* Logo/Header */}
      <div className="p-6 border-b border-border">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <Shield className="text-primary-foreground text-lg" />
          </div>
          <div>
            <h1 className="text-lg font-semibold text-foreground">Access Control</h1>
            <p className="text-xs text-muted-foreground">Middleware Dashboard</p>
          </div>
        </div>
      </div>

      {/* Navigation Menu */}
      <nav className="flex-1 p-4 space-y-2">
        {navigation.map((item) => {
          const Icon = item.icon;
          const isActive = location === item.href;

          return (
            <Link key={item.name} href={item.href}>
              <div
                className={cn(
                  "flex items-center space-x-3 px-3 py-2 rounded-md transition-colors cursor-pointer",
                  isActive
                    ? "bg-primary text-primary-foreground"
                    : "text-muted-foreground hover:text-foreground hover:bg-secondary"
                )}
                data-testid={`nav-${item.name.toLowerCase().replace(/\s+/g, '-')}`}
              >
                <Icon className="w-5 h-5" />
                <span>{item.name}</span>
              </div>
            </Link>
          );
        })}
      </nav>

      {/* System Status */}
      <div className="p-4 border-t border-border">
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <span className="text-sm text-muted-foreground">Dahua Connection</span>
            <div className="flex items-center">
              <StatusIndicator status={getServiceStatus('dahua')} />
              <span className={cn(
                "text-xs",
                getServiceStatus('dahua') === 'online' ? "text-accent" : "text-destructive"
              )}>
                {getServiceStatus('dahua') === 'online' ? 'Online' : 'Offline'}
              </span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm text-muted-foreground">Microsoft Graph</span>
            <div className="flex items-center">
              <StatusIndicator status={getServiceStatus('microsoft-graph')} />
              <span className={cn(
                "text-xs",
                getServiceStatus('microsoft-graph') === 'online' ? "text-accent" : "text-destructive"
              )}>
                {getServiceStatus('microsoft-graph') === 'online' ? 'Connected' : 'Disconnected'}
              </span>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm text-muted-foreground">Server Health</span>
            <div className="flex items-center">
              <StatusIndicator status={getServiceStatus('server')} />
              <span className={cn(
                "text-xs",
                getServiceStatus('server') === 'online' ? "text-accent" : "text-destructive"
              )}>
                {getServiceStatus('server') === 'online' ? 'Healthy' : 'Unhealthy'}
              </span>
            </div>
          </div>
        </div>
      </div>
    </aside>
  );
}

client/src/components/layout/topbar.tsx.content:
import { RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { queryClient } from "@/lib/queryClient";

interface TopbarProps {
  title: string;
  description: string;
}

export default function Topbar({ title, description }: TopbarProps) {
  const handleRefresh = () => {
    queryClient.invalidateQueries();
  };

  return (
    <header className="bg-card border-b border-border px-6 py-4">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-foreground">{title}</h1>
          <p className="text-sm text-muted-foreground">{description}</p>
        </div>
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-accent rounded-full pulse-animation"></div>
            <span className="text-sm text-muted-foreground">Live Monitoring</span>
          </div>
          <Button
            onClick={handleRefresh}
            className="flex items-center space-x-2"
            data-testid="button-refresh"
          >
            <RefreshCw className="w-4 h-4" />
            <span>Refresh</span>
          </Button>
        </div>
      </div>
    </header>
  );
}

